import React, {useState} from "react";
import { Link } from "react-router-dom";

const Menu = () => {
  const [selectedOption, setSelectedOption] = useState(0);
  const [profileOpen, setProfileOpen] = useState(false);

  const handleOptionClick = (index) => {
    setSelectedOption(index);
  }

  const toggleProfile = () => {
    setProfileOpen(!profileOpen);
  }

  const menuClass = "menu";
  const activeMenuClass = "menu selected";

  return (
    <div className="menu-container">
      <img src="/logo.png" alt="Logo" className="logo"/>
      <div className="menus">
        <ul>
          <li>
            <Link style={{textDecoration: "none"}} to="/" onClick={() => handleOptionClick(0)}></Link>
            <p>Dashboard</p>
          </li>
          <li>
            <Link style={{textDecoration: "none"}} to="/" onClick={() => handleOptionClick(1)}></Link>
            <p>Orders</p>
          </li>
          <li>
            <Link style={{textDecoration: "none"}} to="/" onClick={() => handleOptionClick(2)}></Link>
            <p>Holdings</p>
          </li>
          <li>
            <Link style={{textDecoration: "none"}} to="/" onClick={() => handleOptionClick(3)}></Link>
            <p>Positions</p>
          </li>
          <li>
            <Link style={{textDecoration: "none"}} to="/" onClick={() => handleOptionClick(4)}></Link>
            <p>Funds</p>
          </li>
          <li>
            <Link style={{textDecoration: "none"}} to="/" onClick={() => handleOptionClick(5)}></Link>
            <p>Apps</p>
          </li>
        </ul>

      </div>
    </div>
  );
};

export default Menu;
